import React, { Component } from 'react'
import axios from 'axios';

class City extends Component {
    state = {
        city: null
    }
    componentDidMount() {
        let id = this.props.match.params.city_id;
        axios.get('http://localhost:8080/cities/all' + id)
    }
render() {

    const city = this.state.city ? (
        <div className="city">
            <h3>{this.state.city.name}</h3>
            <p>{this.state.city.country}</p>
        </div>
    ) : (
            <div className="center">
                Loading City...
            </div>
        )
    return (
        <div className="container cities-container">
            {city}
        </div>
    )
}
}


export default City;
