import React, { Component } from 'react'
import '../../index.css'
import { MDBContainer, MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import { Link } from 'react-router-dom'
import SearchBar from '../functional/SearchBar';
import axios from 'axios'

class CitiesPage extends Component {
    state = {
        cities: []
    }
    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then(res => {
                console.log(res)
                this.setState({
                    citites: res.data
                })
            })

    }
    render() {
        const { cities } = this.state;
        const cityList = cities.length ? (
            cities.map(post => {
                return (
                    <MDBCol key={city.id}>
                        <Link to={'/' + city.id}>
                            <MDBCard className="city-card" style={{ width: "100%" }}>
                                <MDBCardImage className="img-fluid" src="" waves />
                                <MDBCardBody>
                                    <MDBCardTitle>{city.name}</MDBCardTitle>
                                    <MDBCardText>
                                        {city.country}
                                    </MDBCardText>
                                    <MDBBtn href="#" outline color="info">See itineraries</MDBBtn>
                                </MDBCardBody>
                            </MDBCard>
                        </Link>
                    </MDBCol>

                )
            })
        ) : (
                <div>No cities yet</div>
            )
        return (
            <MDBContainer className="content-container">
                <SearchBar />
                {cityList}
            </MDBContainer>
        )
    }
}

export default CitiesPage;
