import React from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import data from '../../assets/json/data'
import { Link} from 'react-router-dom'
class CityCard extends React.Component {
    state = {
        cities: []
    }
    componentDidMount() {
        this.setState({
            cities: data
        })
    }
    render() {
        const { cities } = this.state;
        return (
            <MDBCol>
                {cities.map(city => (
                    <MDBCard className="city-card" style={{ width: "100%" }} key={city.id}>
                        <MDBCardImage className="img-fluid" src={city.image} waves />
                        <MDBCardBody>
                            <MDBCardTitle>{city.name}</MDBCardTitle>
                            <MDBCardText>
                                {city.country}
                            </MDBCardText>
                            <MDBBtn href="#" outline color="info">See itineraries</MDBBtn>
                        </MDBCardBody>
                    </MDBCard>
                ))}
            </MDBCol>
        )
    }
}

export default CityCard;
