import { FETCH_CITIES_PENDING, FETCH_CITIES_SUCCESS, FETCH_CITIES_ERROR } from '../actions/citiesAction'

const initState = { // It holds all the data you for the application in one place
    pending: false,
    cities: [],
    error: null
}

export function citiesReducer(state = initialState, action) {
    switch (action.type) {
        case FETCH_CITIES_PENDING:
            return {
                ...state,
                pending: true
            }
        case FETCH_CITIES_SUCCESS:
            return {
                ...state,
                pending: false,
                cities: action.payload
            }
        case FETCH_CITIES_ERROR:
            return {
                ...state,
                pending: false,
                error: action.error
            }
        default:
            return state;
    }
}

export const getCities = state => state.cities;
export const getCitiesPending = state => state.pending;
export const getCitiesError = state => state.error;
