import React from "react";
import { MDBCarousel, MDBCarouselCaption, MDBCarouselInner, MDBCarouselItem, MDBView, MDBMask, MDBContainer } from
    "mdbreact";
import data from '../../assets/json/data'
import '../../index.css'

const citiesData = data;

const CarouselPage = () => {
    return (
        <MDBContainer>
            <MDBCarousel
                activeItem={1}
                length= {citiesData.length}
                showControls={true}
                showIndicators={true}
                className="z-depth-1"
            >
                <MDBCarouselInner>
                    {citiesData.map((city, i) => (
                        <MDBCarouselItem itemId={i}>
                            <MDBView>
                                <img
                                    className="d-inline-block w-40 h-100"
                                    src={city.image}
                                    alt="First slide"
                                />
                                <MDBMask overlay="black-light" />
                            </MDBView>
                            <MDBCarouselCaption>
                                <h3 className="h3-responsive">{city.name}</h3>
                                <p>{city.country}</p>
                            </MDBCarouselCaption>
                        </MDBCarouselItem>
                    ))}
                </MDBCarouselInner>
            </MDBCarousel>
        </MDBContainer>
    );
}

export default CarouselPage;
