import React, { Component } from 'react'
import '../../index.css'
import { MDBContainer, MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import SearchBar from '../functional/SearchBar';
import data from '../../assets/json/data'

class CitiesPage extends Component {
    state = {
        cities: []
    }
    componentDidMount() {
        this.setState({
            cities: data
        })
    }
    render() {
        const { cities } = this.state;
        const cityList = cities.length ? (
            cities.map(city => {
                return (
                    <MDBCol>
                        <MDBCard className="city-card" style={{ width: "100%" }} key={city.id}>
                            <MDBCardImage className="img-fluid" src={city.image} waves />
                            <MDBCardBody>
                                <MDBCardTitle>{city.name}</MDBCardTitle>
                                <MDBCardText>
                                    {city.country}
                                </MDBCardText>
                                <MDBBtn href="#" outline color="info">See itineraries</MDBBtn>
                            </MDBCardBody>
                        </MDBCard>

                    </MDBCol>
                )
            })
        ) : (
                <div>No cities yet</div>
            )
        return (
            <MDBContainer className="content-container">
                <SearchBar />
            </MDBContainer>
        )
    }
}

export default CitiesPage;
