import React from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';

const CityCard = (props) => {
    return (
        <MDBCol>
            {citiesData.map((city, i) => (
                <MDBCard className="city-card" style={{ width: "100%" }} key={i}>
                    <MDBCardImage className="img-fluid" src={city.image} waves />
                    <MDBCardBody>
                        <MDBCardTitle>{city.name}</MDBCardTitle>
                        <MDBCardText>
                            {city.country}
                        </MDBCardText>
                        <MDBBtn href="#" outline color="info">See itineraries</MDBBtn>
                    </MDBCardBody>
                </MDBCard>
            ))}
        </MDBCol>
    )
}

export default CityCard;
