import React from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import data from '../../assets/json/data'
const citiesData = data;

const CityCard = () => {
    return (
        <MDBCol>
            {citiesData.map((city, i) => (
                <MDBCard style={{ width: "21rem" }} key={i}>
                    <MDBCardImage className="img-fluid" src={city.image} waves />
                    <MDBCardBody>
                        <MDBCardTitle>{city.name}</MDBCardTitle>
                        <MDBCardText>
                            {city.country}
                        </MDBCardText>
                        <MDBBtn href="#" outline >Discover More</MDBBtn>
                    </MDBCardBody>
                </MDBCard>
            ))}
        </MDBCol>
    )
}

export default CityCard;
